import React from 'react';

function TopicDisplay({ subjectId }) {
  const [topics, setTopics] = useState(null)
    const isSubjectSelected = true;
  
    let content;
    if (isSubjectSelected) {
      const topics = [
        {id: 1, title: "Topic 1"}
      ];
      content = (
        <ul style={{listStyle: "none"}}>
          {topics.map(topic => (
            <li key={topic.id}>{topic.title}</li>
          ))}
        </ul>
      );
    }

    useEffect(() => {
      const fetchSubjectsData = async () => {
          try {
              // Make an API request to fetch the subject data for the selected subject
              const response = await fetch(`/api/curriculum/${globalCountryId}/subject/${subjectId}`);
              const data = await response.json();

              // Set the subject state with the fetched data
              setTopics(data.topics);
          } catch (error) {
              console.error('Error fetching topics data:', error);
          }
      };

      fetchSubjectsData();

  }, [globalCountryId]);

    return (
      <button>
        {content}
      </button>
    );
  }

export default TopicDisplay;
