import React from 'react';

const TestimonialSection = () => {
  const testimonials = [
    // {
    //   id: 1,
    //   name: 'John Doe',
    //   quote: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla quis lorem ut libero malesuada feugiat.',
    //   country: 'Kenya',
    //   image: 'image1.jpg',
    //   gradient: 'linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.3))'
    // },
    {
      id: 1,
      name: 'Jane Smith',
      quote: 'Praesent commodo cursus magna, vel scelerisque nisl consectetur et. Morbi justo mauris, consequat quis ornare a, iaculis eget risus.',
      country: 'Nigeria',
      image: 'image1.jpg',
      gradient: 'linear-gradient(rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.3))'
    },
  ];

  return (
    <section className="testimonial-section">
      <div className="container">
        <h2 className="section-title">Testimonials</h2>
        <div className="testimonial-grid">
          {testimonials.map((testimonial) => (
            <div key={testimonial.id} className="testimonial">
              <div className="testimonial-image" style={{ backgroundImage: `${testimonial.gradient}, url(${testimonial.image})` }}></div>
              <p className="quote">{testimonial.quote}</p>
              <p className="name">{testimonial.name}</p>
              <p className="country">{testimonial.country}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TestimonialSection;
