import React, { useState } from 'react';
import Draggable from 'react-draggable';

const SwipeableList = () => {
  const [lists] = useState([
    {
      id: 1,
      country: 'Algeria',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'Science', 'History'],
      region: 'North Africa'
    },
    {
      id: 2,
      country: 'Egypt',
      curriculum: 'National Curriculum',
      subjects: ['Arabic', 'English', 'Geography'],
      region: 'North Africa'
    },
    {
      id: 3,
      country: 'Nigeria',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'English', 'Biology'],
      region: 'West Africa'
    },
    {
      id: 4,
      country: 'Ghana',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'English', 'History'],
      region: 'West Africa'
    },
    {
      id: 5,
      country: 'Kenya',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'Swahili', 'Science'],
      region: 'East Africa'
    },
    {
      id: 6,
      country: 'Tanzania',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'Swahili', 'Geography'],
      region: 'East Africa'
    },
    {
      id: 7,
      country: 'South Africa',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'English', 'History'],
      region: 'Southern Africa'
    },
    {
      id: 8,
      country: 'Zimbabwe',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'English', 'Biology'],
      region: 'Southern Africa'
    },
    {
      id: 9,
      country: 'Senegal',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'French', 'History'],
      region: 'West Africa'
    },
    {
      id: 10,
      country: 'Ivory Coast',
      curriculum: 'National Curriculum',
      subjects: ['Mathematics', 'French', 'Geography'],
      region: 'West Africa'
    }
    // Add more countries from each region as needed
  ]);
  const [selectedList, setSelectedList] = useState(lists[0]);

  const handlePrevList = () => {
    const currentIndex = lists.findIndex((list) => list === selectedList);
    const prevList = lists[currentIndex - 1] || lists[lists.length - 1];
    setSelectedList(prevList);
  };

  const handleNextList = () => {
    const currentIndex = lists.findIndex((list) => list === selectedList);
    const nextList = lists[currentIndex + 1] || lists[0];
    setSelectedList(nextList);
  };

  const handleListChange = (e) => {
    const listId = e.target.value;
    const selectedList = lists.find((list) => list.id === parseInt(listId));
    setSelectedList(selectedList);
  };

  return (
    <div className="list-container">
      <div className="swipe-buttons">
        <button onClick={handlePrevList}>&lt;</button>
        <button onClick={handleNextList}>&gt;</button>
      </div>
      <Draggable axis="x">
        <ul className="swipe-list">
          <li>
            <select value={selectedList.id} onChange={handleListChange}>
              {lists.map((list) => (
                <option key={list.id} value={list.id}>
                  {list.country}
                </option>
              ))}
            </select>
          </li>
          {selectedList && (
            <>
              <li>{selectedList.country}</li>
              <li>{selectedList.curriculum}</li>
              {selectedList.subjects.map((subject, index) => (
                <li key={index}>{subject}</li>
              ))}
            </>
          )}
        </ul>
      </Draggable>
    </div>
  );
};

export default SwipeableList;
